using System;
using System.IO;
using RocksmithToolkitLib.Extensions;

namespace RocksmithToolkitLib.DLCPackage
{
    public class InlayNif
    {
        private string nifHex = String.Empty;

        public InlayNif() { }

        public InlayNif(DLCPackageData info)
        {
            string nFileName, n1, n2, n2a, n2b;

            // CRITICAL - naming dependencies
            nFileName = String.Format("inlay_{0}.dds", info.Inlay.DLCSixName);

            // HEX strings to make the nif file
            if (info.Inlay.Frets24)
            {
                if (info.Inlay.Colored)
                {
                    n1 = "";
                    n2a = "";
                    n2b = "";
                    n2 = n2a + n2b;
                }
                else
                {
                    n1 = "";
                    n2a = "";
                    n2b = "";
                    n2 = n2a + n2b;
                }
            }
            else
            {
                if (!info.Inlay.Colored)
                {
                    n1 = "";
                    n2 = "";
                }
                else
                {
                    n1 = "";
                    n2 = "";
                }
            }

            nifHex = n1 + nFileName.ToHex() + n2;
        }

        public void Serialize(Stream outStream)
        {
            var nifBytes = nifHex.ToByteArray();
            outStream.Write(nifBytes, 0, nifBytes.Length);
        }
    }
}
